import {
	PLAY_NOTIFICATION,
	STOP_NOTIFICATION,
	SET_PLAYER_QUE,
	SET_PLAYER_STATE,
	SET_PLAYER_STATUS
} from '../actions/actionsTypes';

const soundsReducer = (state = {
	notification: false,
	player: {
		current: -1, // номер текущего трека в очереди
		current_type: undefined, // тип текущего трека
		status: false // ? Играет : Остановлено
	},
	que: []
}, action) => {
	switch (action.type) {
		case PLAY_NOTIFICATION:
			return Object.assign({}, state, {notification: true});
		case STOP_NOTIFICATION:
			return Object.assign({}, state, {notification: false});
		case SET_PLAYER_QUE:
			return Object.assign({}, state, {
				que: action.que
			});
		case SET_PLAYER_STATE:
			return Object.assign({}, state, {
				player: Object.assign({}, state.player, action.player),
				que: action.que ? action.que : state.que
			});
		case SET_PLAYER_STATUS:
			return Object.assign({}, state, {
				player: Object.assign({}, state.player, {status: action.status})
			});
		default:
			return state;
	}
}

export default soundsReducer;
