// import initialState from './initialState';
import {
	SET_IN_DATABASE_BY_PATH
} from '../actions/actionsTypes';
import {
	assignToPathFromObjectToValue,
	appendInObject
} from "../helpers";
import {
	USERS_KEY,
	POST_KEY
} from '../actions/keys';

const databaseReducer = (state={
	[USERS_KEY]: {}
}, action) => {
	let new_state = {...state};
	console.log(action);
    switch(action.type) {
		case MERGE_ENTITIES_IN_DATABASE:
			[
				POST_KEY
			].forEach(key => {
				if (action.entities[key]) {
					new_state[key] = appendInObject(new_state[key], action.entities[key], {deep_level: 1});
				}
			})
			return new_state;
		case SET_IN_DATABASE_BY_PATH:
			return Object.assign({}, state, assignToPathFromObjectToValue(new_state, action.path, action.data));
		default: return state;
	}
}

export default databaseReducer;
