/**
 * @param {String} url
 * Делает запрос по заданному к API с помощью fetch. Также обновляет статус "онлайн" каждый 30 секунд, если запросов в течении этого времени не было
 * @param {String} [method='get']
 * @param {Object} params
 * @param {Boolean} [token=true] is auth needed
 * @param {Boolean} is_url_full
 * @param {Object} options:
 * - parse_type - json | text
 * - token
 * - abortController
 * @return {Promise}
 */
export function request(url, method = 'GET', params) {
	let body;
	if ( params ) {
		switch (method) {
			case 'GET':
			case 'get':
			  url = url;
			  // saveLog(url);
			  break;
			case 'POST':
			case 'post':
			  body = JSON.stringify(params);
			  // saveLog(body);
			  break;
		}
	}
	return fetch(url, {
		method: method,
		body: body,
	}).then(response => {
		if (response.ok) {
			let parser = {
				json: () => response.json(),
				text: () => response.text(),
			};
			return parser();
		}
		return response.json();
	}).catch(error => {
		return Promise.reject(error);
	});
}
