import initialState from './initialState';
import 
	APPEND_IN_DATABASE_BY_PATH
from '../actions/keys';
import {
	appendToPathFromObjectToValue
} from "../helpers";

export default function databaseReducer(state = initialState.ui, action) {
    switch(action.type) {
        case "TOGGLE_FORM": {
				return {
					...state, isContactFormHidden: !state.isContactFormHidden
					}

			}
			case APPEND_IN_DATABASE_BY_PATH:
			return Object.assign({}, state, appendToPathFromObjectToValue(new_state, action.path, action.data, action.options));
		default: return state;
	}
}
