import React, {Component} from 'react'
import Helmet from 'react-helmet'
import { connect } from 'react-redux'
import Preloader from './blocks/Preloader'
import {incrementPartnerLink} from '../actions/actions'
import {PARTNER_KEY} from '../actions/keys'
import {setPartnerLink} from '../cookiesDispatchers'

class PartnerResolver extends Component {
	constructor(props) {
	super(props);
	}
	static fetchData({store, url}) {
		return [
			store.dispatch(incrementPartnerLink(url))
		];
	}
	componentDidMount() {
		let url = window.location.href;
		let {id, type, web_url} = this.props.info;
		setPartnerLink(id, url); // добавить ссылку в куки
		if (type === 'product') {
			this.props.history.push(web_url);
		} else if (type === 'shop') {
			this.props.history.push(web_url);
		}
	}
	render() {
		return (
			<div className='content-wrap'>
				<div className='center-column'>
					<Helmet>
						<title>{this.props.info.title}</title>
						<meta name='description' content={this.props.info.description} />
						<meta property='og:title' content={this.props.info.title} />
						<meta property='og:description' content={this.props.info.description} />
					</Helmet>
					<Preloader />
				</div>
			</div>
		);
	}
}

const mapStateToProps = (state) => {
	return {
		info: state.database[PARTNER_KEY]
	}
};

export default connect(
	mapStateToProps
)(PartnerResolver);
