import databaseReducer from './database'

/**
 * Creates array of fetched results - array with ids of entities
 * @param  {Object} [state={}] [description]
 * @param  {[type]} action     [description]
 * @return {[type]}            [description]
 */
import {
	lastError
} from '../actions/actionsTypes';

const environmentReducer = ( state = {
	lastError: '',
}, action ) => {
	let new_state = Object.assign({}, state);
	switch (action.type) {
		case SET_LAST_ERROR:
			new_state.lastError = action.text
			return new_state;

		default:
			return state;
	}
}

export default ( state = {}, action ) => {
	return {
		environment:		environmentReducer(state.environment, action),
		database:				databaseReducer(state.database, action),
	};
}
