import initialState from './initialState';



export default function databaseReducer(state = initialState.ui, action) {
    switch(action.type) {
        case "TOGGLE_FORM": {
				return {
					...state, isContactFormHidden: !state.isContactFormHidden
					}

			}
		default: return state;
	}
}
