import {
	SET_PAGE_HELPERS,
	UNSET_PAGE_HELPERS,
} from '../actions/actionsTypes';

export const makeRandomId = (n = 1) => `${Math.round(Math.random()*n)}${Date.now()}`

const helperReducer = (state = {
	page: [] // список подсказок на этой странице
}, action) => {
	switch (action.type) {
		case SET_PAGE_HELPERS:
			return Object.assign({}, state, {
				page: [...state.page, ...action.helpers]
			});
		case UNSET_PAGE_HELPERS:
			return Object.assign({}, state, {
				page: state.page.filter(i => !action.helpers.includes(i))
			});
		default:
			return state;
	}
}

export default helperReducer;
