import React, {Component, Fragment} from 'react'
import { connect } from 'react-redux'
import Pagination from "react-js-pagination";
import {
	getPosts
} from '../actions/actions.js'
import {
	POSTS_KEY,
	COUNT_PAGES_KEY
} from '../actions/keys';
import {
	getPathFromObject
} from '../helpers.js';
import PostCard from './PostCard'

class ListPosts extends Component {
	constructor(props) {
		super(props);
		this.state = {
			active_page: 1,
			selected: {},
		}
		this.handlePageChange = this.handlePageChange.bind(this);
		this.onSelect = this.onSelect.bind(this);
	  }
	componentDidMount() {
		const id = this.props.match.params.id;
		this.props.getPosts(id);
	}
	  handlePageChange(pageNumber) {
		this.setState({activePage: pageNumber});
		this.props.getUsers(pageNumber);
	  }
	  onSelect(item_id) {
		  console.log(item_id);
		const selected = Object.assign({}, this.state.selected);
			if (!selected[item_id]) {
				const selected = {[item_id]: true};
				this.setState({
					selected, 
				});
			} else {
				this.setState({selected: {}});
			}
	  }
	renderPosts() {
		const posts = this.props.posts;
		return posts.length > 0 ? (
		  <Fragment>
			  <div className='item_list'>  
				  {posts.map((i) => {
						  return (
							  <PostCard 	
								  key={i.id} 
								  params={i} 
								  onSelect={() => this.onSelect(i.id)} 
								  isSelected={this.state.selected[i.id]} />
						  );
				  })}
			  </div>
			  <div className='pagination'>
			  <Pagination
				  hideDisabled
				  activePage={this.state.active_page}
				  itemsCountPerPage={12}
				  totalItemsCount={this.props.pagination.pages}
				  pageRangeDisplayed={10}
				  itemClass='page-item'
				  linkClass='page-link'
				  getPageUrl={(i) => `/?page=${i}`}
				  onChange={this.handlePageChange}
			  />
			  </div>
		</Fragment>
	  ) : null;
	}
	render() {
		return (
<section className="container">
			<div className="right-column">
				{this.renderPosts()}
			</div>
		</section>
		);
	}
}

const mapStateToProps = (state) => {
	return {
		posts : getPathFromObject(state.database, POSTS_KEY, []),
		pagination: getPathFromObject(state.database, `${COUNT_PAGES_KEY}.posts`, []),
	}
  }

  const mapDispatchToProps = (dispatch) => {
	return {
		getPosts(id) {
			return dispatch(getPosts(id));
		},
	}
  }

export default connect(
	mapStateToProps,
	mapDispatchToProps
)(ListPosts);
