import { normalize } from 'normalizr'
import { requestAction } from '../actions/actions';
import { RESPONSE_FAIL } from '../actions/actionsTypes';
import { PRODUCTS_COORDS } from '../actions/keys';
import { request as web_request } from "../../api/request";
import {isNode} from '../helpers';

export const API_CALL = 'API_CALL';

/* eslint-disable */
const request = isNode() ? global._node_request_ : web_request;
/* eslint-enable */
// const handleError = ( errorCode ) => {
// 	switch( errorCode ) {
// 	  case 404:
//       this.props.history.push( '/404' );
//       break;
// 	  default:
//       break;
// 	}
// }

export default store => next => action => {
	let api_call = action[API_CALL];
	if (typeof api_call === 'undefined') {
		return next(action);
	}

	let {
		url,
		method,
		params,
		schema,
		key,
		action_type,
		filters,
		main,
		addition_keys
	} = api_call;

  method = method.toUpperCase();

  if ( filters ) {
		let f = Object.assign({}, store.getState().filters[key]);
		if (f) {

			f.distance = (f.distance === undefined || f.distance > 50) ? 0 : f.distance; // if more than 50 then unlimited
			f.rating = (f.rating !== undefined && f.rating >= 4); // if more than 4 then true
			let ids = [];
			let values = [];
			for (let prop_id in f.properties) {
				if (f.properties[prop_id] != 0) {
					ids.push(prop_id);
					values.push(f.properties[prop_id])
				}
			}
			if (ids.length > 0) {
				params['properties[id]'] = ids.join(',');
				params['properties[value]'] = values.join(',');
			}
			params['filters'] = f;
		}
	}
	if (key === PRODUCTS_COORDS && store.getState().result[key]) {
		params['loaded_count'] = store.getState().result[key].length;
	}

  switch( method ) {
      case 'GET':
      case 'POST':
          break;
      default:
          next( action );
  }
	store.dispatch( requestAction( key ) );
  return request(url, method, params).then(json => {
		if (json.error !== 0) {
			// handleError( json.error, store.dispatch );
			throw new Error(JSON.stringify(json));
		}

		let data = json.response.items;
		if ( addition_keys ) {
			for ( let k of addition_keys ) {
				data = { [ k ]: data }
			}
		}
		data = { data: normalize( { [ key ]: data }, schema ) };
		if ( json.response.last_id ) {
			data.last_id = json.response.last_id;
		} else if ( json.response.last_id === 0 ) {
			data.last_id = null;
		}

		if ( json.response.all_count ) {
			data.all_count = json.response.all_count;
		}
		return data;
	}).then(json => next({
		type: action_type,
		response: json,
		key,
	}), error => {
		let message = JSON.parse(error.message);
		return next({
			type: RESPONSE_FAIL,
			main: main || false,
			error: message.error_text,
			error_status: message.error,
			key,
		});
	})
}
