import '../styles/App.css';
import React from 'react'

const Comments = ({items}) => {
	return (
		<div className='comments-container'>
			{items.map((i) => {
				return (
				<div key={i.id}>
					<span className='name'>{i.name}</span>
					<p>
						{i.body}
					</p>
				</div>
				);
			})}
		</div>
	);
}
export default Comments
