import React, {Component, Fragment} from 'react'
import { connect } from 'react-redux'
import {
	getPost,
	getComments
} from '../actions/actions.js'
import {
	POST_KEY,
	COMMENTS_KEY
} from '../actions/keys';
import {
	getPathFromObject
} from '../helpers.js';
import Comments from './Comments.js';

class PostPage extends Component {
	constructor(props) {
		super(props);
		this.state = {
			show_comments: false
		}
		this.onClick = this.onClick.bind(this);
	  }
	componentDidMount() {
		const id = this.props.match.params.id;
		this.props.getPost(id);
	}
	onClick() {
		this.props.getComments(this.props.post.id)
		.then(d => {
			console.log(d);
			this.setState({show_comments: true})
		})
	}
	renderComments() {
		return (
			<Fragment>
				vvf
			</Fragment>
		);
	}
	render() {
		return (
			<div>
				{this.props.post && (
					<div>
						<h1>
							{this.props.post.title}
						</h1>
						<div>
							{this.props.post.body}
						</div>
						<button onClick={this.onClick} className='button'>Показать комментарии</button>
					</div>
				)}
				{this.state.show_comments && <Comments/>}
			</div>
		);
	}
}

const mapStateToProps = (state) => {
	return {
		post : getPathFromObject(state.database, POST_KEY, []),
		comments: getPathFromObject(state.database, COMMENTS_KEY, []),
	}
  }

  const mapDispatchToProps = (dispatch) => {
	return {
		getPost(id) {
			return dispatch(getPost(id));
		},
		getComments(id) {
			return dispatch(getComments(id));
		}
	}
  }

export default connect(
	mapStateToProps,
	mapDispatchToProps
)(PostPage);
