import '../styles/App.css';
import React from 'react'

const UserCard = ({params, onSelect, selected}) => {
	const is_selected = selected ? selected.includes(params.id) : false;
	return (
		<div key={params.id} className='user-card'>
			<div style={{cursor: 'pointer'}} onClick={onSelect}>
			<input className={is_selected ? 'select_active' : 'select'}
					type="checkbox"
					checked={is_selected ? true : false}
					// onChange={() => {}}
					// name={this.props.name}
					onClick={this.onSelect}
					// disabled={this.props.disabled} 
					/>
				<span className='user_name'>{params.name}</span>
			</div>	
		</div>
	)
}
export default UserCard
