import '../styles/App.css';
import React, {Component, Fragment} from 'react'
import { connect } from 'react-redux'
import Pagination from "react-js-pagination";
import {
	addUsers
} from '../actions/actions.js'
import {
	USERS_KEY,
	META_KEY
} from '../actions/keys';
import {
	getPathFromObject
} from '../helpers.js';
import Menu from './Menu'
import UserCard from './UserCard'

class Common extends Component {
	constructor(props) {
		super(props);
		this.state = {
			show_users: false,
			active_page: 1
		}
		this.onClick = this.onClick.bind(this);
		this.handlePageChange = this.handlePageChange.bind(this);
	  }

	  componentDidMount() {
		this.props.getUsers()
	  }
	  onClick() {
		this.setState({show_users: true})
	  }
	  handlePageChange(pageNumber) {
		this.setState({activePage: pageNumber});
		this.props.getUsers(pageNumber);
	  }
	  renderUsers() {
		const users = this.props.users;
		  return users.length > 0 ? (
			<Fragment>
				<div className='user_list'>  
					{users.map((i) => {
							return (
									<UserCard key={i.id} params={i}/>
							);
					})}
				</div>
				<div className='pagination'>
				<Pagination
					hideDisabled
					activePage={this.state.active_page}
					itemsCountPerPage={12}
					totalItemsCount={this.props.pagination.pages}
					pageRangeDisplayed={10}
					itemClass='page-item'
					linkClass='page-link'
					getPageUrl={(i) => `/?page=${i}`}
					onChange={this.handlePageChange}
				/>
				</div>
		  </Fragment>
		) : null;
	  }
	  render() {
		return (
			<section className="container">
				 <div className="left-column">
				 	<Menu onClick={this.onClick}/>
				 </div>
				 <div className="right-column">
				 	{this.state.show_users && this.renderUsers()}
				 </div>
			</section>
		);
	  }
	}

	const mapStateToProps = (state) => {
		return {
			users : getPathFromObject(state.database, USERS_KEY, []),
			pagination: getPathFromObject(state.database, `${META_KEY}.pagination`, []),
		}
	  }

	  const mapDispatchToProps = (dispatch) => {
		return {
			getUsers(pageNumber) {
				return dispatch(addUsers(pageNumber));
			},
		}
	  }

export default connect(
	mapStateToProps,
	mapDispatchToProps
)(Common);
