import React, {Component, Fragment} from 'react'
import { connect } from 'react-redux'
import {
	getPost,
	getComments
} from '../actions/actions.js'
import {
	POST_KEY,
	COMMENTS_KEY
} from '../actions/keys';
import {
	getPathFromObject
} from '../helpers.js';
import Comments from './Comments.js';

class PostPage extends Component {
	constructor(props) {
		super(props);
		this.state = {
			post_id: '',
			show_comments: false
		}
		this.onClick = this.onClick.bind(this);
	  }
	componentDidMount() {
		const id = this.props.match.params.id;
		this.props.getPost(id)
		.then(() => {
			this.setState({post_id: this.props.post[0].id})
		});
	}
	onClick() {
		this.setState({show_comments: true})
	}
	renderComments() {
		return (
			<Fragment>
				vvf
			</Fragment>
		);
	}
	render() {
		return (
			<div>
				{this.props.post.length > 0 && (
					<div>
						{this.props.posts.map((i) => {
							return (
								<Fragment>
									<h1>
										{i.title}
									</h1>
									<div>
										{i.body}
									</div>
									<button onClick={this.onClick} className='button'>Показать комментарии</button>
								</Fragment>
							);
					})}
					</div>
				)}
				{this.state.show_comments && <Comments/>}
			</div>
		);
	}
}

const mapStateToProps = (state) => {
	return {
		post : getPathFromObject(state.database, POST_KEY, []),
		comments: getPathFromObject(state.database, COMMENTS_KEY, []),
	}
  }

  const mapDispatchToProps = (dispatch) => {
	return {
		getPost(id) {
			return dispatch(getPost(id));
		},
		getComments(id) {
			return dispatch(getComments(id));
		}
	}
  }

export default connect(
	mapStateToProps,
	mapDispatchToProps
)(PostPage);
